from aiogram import F, Routerfrom aiogram.fsm.context import FSMContextfrom aiogram.fsm.state import StatesGroup, Statefrom aiogram.types import InlineKeyboardMarkup, InlineKeyboardButton, Message, CallbackQuery, FSInputFilefrom subjects.history.historical_events import handle_year_requestfrom subjects.history.keyboard_history import history_kb, back_history_kbrouter = Router()class historical_events(StatesGroup):    date = State()@router.message(F.text == '–ò—Å—Ç–æ—Ä–∏—è üìÜ')async def history1(message: Message):    await message.answer('–í—ã–±–µ—Ä–∏—Ç–µ –ø—É–Ω–∫—Ç', reply_markup=history_kb)@router.callback_query(F.data == 'back_history')async def history2(callback: CallbackQuery):    await callback.message.edit_text('–í—ã–±–µ—Ä–∏—Ç–µ –ø—É–Ω–∫—Ç', reply_markup=history_kb)@router.callback_query(F.data == 'date')async def history3(callback: CallbackQuery, state: FSMContext):    await state.set_state(historical_events.date)    await callback.message.edit_text('–í–≤–µ–¥–∏—Ç–µ –¥–∞—Ç—É –≤ —Ñ–æ—Ä–º–∞—Ç–µ YYYY (–ø–æ–∫–∞ —Ç–æ–ª—å–∫–æ XVI –≤–µ–∫)', reply_markup=back_history_kb)@router.message(historical_events.date)async def history4(message: Message, state: FSMContext):    await handle_year_request(message)    await state.clear()